import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./login.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./login.component.css?ngResource";
import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { FormControl, FormGroup, Validators } from "@angular/forms";
import { UIService } from "../../shared/ui.service";
import { Subscription } from "rxjs";
let LoginComponent = class LoginComponent {
    constructor(authService, uiService) {
        this.authService = authService;
        this.uiService = uiService;
        this.loginForm = new FormGroup({});
        this.isLoading = false;
        this.loadingSubscription = new Subscription();
    }
    ngOnInit() {
        this.loadingSubscription =
            this.uiService
                .loadingStateChange$
                .subscribe((isLoadingState) => {
                this.isLoading = isLoadingState;
            }, (error) => {
                console.log('error :', error);
            });
        this.loginForm = new FormGroup({
            email: new FormControl('', {
                validators: [
                    Validators.required,
                    Validators.email
                ]
            }),
            password: new FormControl('', {
                validators: [
                    Validators.required
                ]
            })
        });
    }
    onSubmit() {
        if (!this.loginForm.valid) {
            return;
        }
        this.authService.login({
            email: this.loginForm.value.email,
            password: this.loginForm.value.password
        })
            .subscribe(isLogin => {
            console.log('%c login subscribe: ', 'background: white; ' +
                'color: #000; ' +
                'padding: 10px; ' +
                'border: 1px solid red');
            console.log(isLogin);
            this.authService.authChangeNotifier(isLogin);
            if (isLogin) {
                this.authService.authSuccessfully();
            }
        }, error => {
            console.log('error');
            console.log(error);
            this.uiService.showSnackBar("error when login", undefined, 3000);
        });
    }
    ngOnDestroy() {
        this.loadingSubscription.unsubscribe();
    }
